import './index.css'


const App = () => {

    let [Task , updateTask] = useState(val);

    const InputEvent=(e)=>{
     let Val =   e.target.value;
updateTask(val)
    }









    return(
    <>
        <div className="main_div">
            <div className="center_div">
                <br />
                <h1>ToDo List</h1>
                <br />
                <input type="text" placeholder="Add Tasks"  value={val} onChange={InputEvent}/>
                <button>+</button>
                <ol>
                    <li>{Task}</li>
                </ol>
            </div>
        </div>
    </>
    )
}

export default App;
