const createNote = (props) => {

    const [note, setNote] = useState({
        title:'',
        content:''
    });


    const inputEvent=(e)=>{

        const{name , value} =e.target;
        setNote((preValue)=>{
            return{
                ...preValue,
                 [name]:value
            }
        })
    }

    const AddNote=()=>{

    }


    return (
        <>
            <form >
                <input type="text" placeholder="Enter Your Title" name="title" value={note.title} onChange={inputEvent}/>
                <textarea placeholder="Write Your Note" rows="" column="" name="content" value={note.content}  onChange={inputEvent}/>
                <button onClick={AddNote}>+</button>
            </form>

            <div>
                {/* <h3>Your Title:{titleVal}</h3> */}
                {/* <p>Your Note:{MyNote}</p> */}
                {/* <button>Del</button/> */}

               
            </div>
        </>
    )
}

export default createNote;
