import React, { useState } from "react";
import Input from "./Input";

const Contact = () => {

  const [input, setInput] = useState({
    fullname: "",
    number: "",
    email: "",
    message: ""
  });

  const inputEvent = (e) => {

    let Value = e.target.value;
    let Name = e.target.name;
    console.log(Value);

    setInput((preVal) => {
      return {
        ...preVal,
        [Name]: Value,
      }
    })
  }

  const FormSubmit = (e) => {
    e.preventDefault();
    alert(input.fullname)

  }



  return (
    <>
      <div className="my-3 ">
        <h2 className="text-center ">Contact Us</h2>
      </div>
      <div className="container mt-4">
        <div className="col-md-6 col-10 mx-auto ">
          <form onSubmit={FormSubmit}>
            <Input
              fname="Full Name"
              pholder="Enter Your Name"
              name="fullname"
              value={input.fullname}
              onChange={inputEvent}
            />
            <div className="mb-3">
              <label for="exampleFormControlInput1" className="form-label">Phone Number</label>
              <input type="number" className="form-control" id="exampleFormControlInput1" placeholder="Enter Your Phone Number"
                name="number"
                value={input.number}
                onChange={inputEvent}
              />
            </div>
            <div className="mb-3">
              <label for="exampleFormControlInput1" className="form-label">Email address</label>
              <input type="email" className="form-control" id="exampleFormControlInput1" placeholder="Enter Your Email"
                name="email"
                value={input.email}
                onChange={inputEvent}
              />
            </div>
            <div className="mb-3">
              <label for="exampleFormControlTextarea1" className="form-label">Example textarea</label>
              <textarea placeholder="Message" className="form-control" id="exampleFormControlTextarea1" rows="3"
                name="message"
                value={input.message}
                onChange={inputEvent}
              ></textarea>
            </div>
            <div className="col-auto">
              <button type="submit" className="btn btn-outline-primary  mb-3">Submit</button>
            </div>
          </form>
        </div>
      </div>

    </>
  )
}

export default Contact;
