import React, { useState } from "react";

const App = () => {

    let state = useState();
    let [Name, newName] = useState();  // Name is the single source of imput amd here its a controlled Component.
    let [EnterName, setName] = useState();
    let [Pass, newPass] = useState();
    let [EnterPass, setPass] = useState();



    const InputEvent = (e) => {
        let Name1 = e.target.value;
        newName(Name1)
    }
    
    const InputEvent2 = (e) => {
        let Pass1 = e.target.value;
        newPass(Pass1)
    }
    const ShowName = (e) => {
        e.preventDefault()
        setName(Name)
        setPass(Pass)

    }

    return (
        <>
            <form onSubmit={ShowName}>
                <div>
                    <h1>Hello {EnterName}</h1>
                    <input placeholder="Enter Your Name" type="text" onChange={InputEvent} value={Name} />
                    <input placeholder="Enter Your Password" type="text" onChange={InputEvent2} value={Pass} />
                    <button type="submit">Submit</button>
                </div>
            </form>


        </>
    )
}

export default App;
