import React, { useState } from "react";
import './index.css'


const App = () => {

    let [inputList, setInputList] = useState();
    let [Items, setItems] = useState([]);

    // const InputEvent = (e) => {
    //     let Val = e.target.value;
    // }

    const AddTask = (e) => {
        setItems((oldItems)=>{
            return [...oldItems , inputList]
        })
    }


    return (
        <>
            <div className="main_div">
                <div className="center_div">
                    <br />
                    <h1>ToDo List</h1>
                    <br />
                    <input type="text" placeholder="Add Tasks" value={inputList} onChange={InputEvent} />
                    <button onClick={AddTask}>+</button>
                    <ol>
                        {Items.map((item) => {
                            return <li>{item}</li>
                        })}
                    </ol>
                </div>
            </div>
        </>
    )
}

export default App;
