import React, { useState } from "react";

const ComA = () => {
    const [num, setNum] = useState()
    return (
        <>
            <h1>You Choose: {num}</h1>
            <select value={num} onChange={(e) => {
                setNum(e.target.value)
            }}>
                <option>1</option>
                <option>2</option>
                <option>3</option>
                <option>4</option>
                <option>5</option>
            </select>
        </>
    )
}

export default ComA;
