import React, { useState } from "react";

let App = () => {

    const [num, setNum] = useState(0);

    let IncNum = () => {
        setNum(num + 1)
    }
    let DecNum = () => {
        // do{

            setNum(num - 1);
        // }while(num==0)
    }

    return (
        <>

            <h1>{num}</h1>
            <button onClick={IncNum}> + </button>
            <button onClick={DecNum}> - </button>

        </>
    )
}
export default App;
