import React, { useState } from "react";

const Note = (props) => {

    const [note, setNote] = useState({
        title: '',
        content: ''
    });


    const inputEvent = (e) => {

        const { name, value } = e.target;
        setNote((preValue) => {
            return {
                ...preValue,
                [name]: value
            }
        })
    }


    const AddNote = (e) => {
        props.passNote(note);
        setNote({
            title: '',
            content: ''
        });
        e.preventDefault();
    }

    return (
        <>
            <form >
                <input type="text" placeholder="Enter Your Title" name="title" value={note.title} onChange={inputEvent} />
                <textarea placeholder="Write Your Note" rows="" column="" name="content" value={note.content} onChange={inputEvent} />
                <button onClick={AddNote}>+</button>
            </form>

        </>
    )
}

export default Note;
