import React from "react";
import { Route, Routes } from 'react-router-dom'  // Step 3
import About from './About'
import Contact from './Contact'

//* React Router ka main kaam hota hai ki jab bhi hum url pe kuch bhi type krte hai agr vo hamare existing comp se match 
//* krta hai to isko ye render karata hai  

const App = () => {

    let Name =()=>{
        return <h1>Name Page Is Activated</h1>
    }
    let Error404 =()=>{
        return <h1>Page Not Found</h1>
    }
    return (
        <>
            <Routes>
                 <Route exact path="/" Component={About} />   {/* exact se exactly jab '/' ho to hi ush page ko render krna */}
                <Route  exact path="/contact" Component={Contact} ></Route>
                <Route  path="/contact/name" Component={Name} ></Route>
                <Route Component={Error404} ></Route>
            </Routes>
        </>
    )
}


export default App;
