import React, { useState } from "react";
// import AddIcon from '@mui/icons-material/Add';
import AddIcon from '@mui/icons-material/Add';
import AddIcon1 from '@material-ui/icons/Add';

let App = () => {

    const [num, setNum] = useState(0);

    let IncNum = () => {
        setNum(num + 1)
    }
    let DecNum = () => {
        if(num !== 0){
            setNum(num - 1)
        }
        else{
            alert('You Have Reached Zero Cannot Decreased It Now!')
        }
    }

    return (
        <>

            <h1>{num}</h1>
            <button onClick={IncNum}> <AddIcon /> </button>
            <button onClick={DecNum}> - </button>

        </>
    )
}
export default App;
