import React, { useState } from "react";

const QA =(Questions,Answers)=>{

    let [show , setShow] = useState(false);
    return(
<>
    <div>
        <button onClick={()=> setShow(true)}>{show ? '-':'+'}</button>
        <h3>{Questions}</h3>
    </div>
    {
        show &&  <p>{Answers}</p>
    }
   
</>
    )
}
 export default QA;
