import React, { useState } from "react";
import Note from "./NotesInput";
import SaveNote from "./ShowNote";

const App = () => {

    const [addItem, setAddItem] = useState()
    let AddNote = (note) => {
        setAddItem((preData) => {
            return [...preData, addItem]
        })
    }
    return (
        <>
            <Note passNote={AddNote} />
            <SaveNote />
        </>
    )
}

export default App;
