import React, { useState } from "react";

const App = () => {


    
let fullName={
    fname:"vinod",
    lname:"thapa"
}

let bioData={
    id:1,
    ...fullName,
    roll_no:21
}

console.log(bioData);
    let arr1 = ['A1', 'A2', 'A3'];
    // let arr2 = ['B1', 'B2', 'B3'];

    // let Arr = [...arr1, ...arr2];
    let Data = [1, ...arr1, 19 ,"Male"]
    // console.log(Arr);
    console.log(Data);

    let FightingGame = ['F1', 'F2', 'F3'];
let ShoutingGame = ['S1', 'S2', 'S3'];

let Game = [...FightingGame ,...ShoutingGame];
// console.log(Game);

let SurvivalGame = ['BGMI', "PUBG", "Game For Peace", "Call Of Duty"];

let [First, ...remaning] = SurvivalGame;
// console.log(First);
// console.log(remaning);



    let [MyName, newName] = useState({
        fname: '',
        pass: '',
        email: '',
        num: '',
    });

    const InputEvent = (e) => {
        let Val = e.target.value;
        console.log(Val);

        const value = e.target.value;
        const Name = e.target.name;


        newName((preValue) => {
            if (Name === "fname") {
                return {
                    fname: value,
                    pass: preValue.pass,
                    email: preValue.email,
                    num: preValue.num,
                }
            }
            else if (Name === "pass") {
                return {
                    fname: preValue.fname,
                    pass: value,
                    email: preValue.email,
                    num: preValue.num,
                }
            }
            else if (Name === "email") {
                return {
                    fname: preValue.fname,
                    pass: preValue.pass,
                    email: value,
                    num: preValue.num,
                }
            }
            else {
                return {
                    fname: preValue.fname,
                    pass: preValue.pass,
                    email: preValue.email,
                    num: value,
                }
            }
        })
    }
    const ShowName = (e) => {
        e.preventDefault();
        alert('Submited')
    }

    return (
        <>
            <form onSubmit={ShowName}>
                <div>
                    <h1>Hello {MyName.fname} {MyName.pass}</h1>
                    <p>{MyName.email}</p>
                    <p>{MyName.num}</p>
                    <input placeholder="Enter Your Name" type="text" onChange={InputEvent} value={MyName.fName} name="fname" />
                    <input placeholder="Enter Your Password" type="text" onChange={InputEvent} value={MyName.Pass} name="pass" />
                    <input placeholder="Enter Your Email" type="email" onChange={InputEvent} value={MyName.Email} name="email" />
                    <input placeholder="Enter Your Phone" type="number" onChange={InputEvent} value={MyName.Num} name="num" />

                    <button type="submit">Submit</button>
                </div>
            </form>


        </>
    )
}

export default App;
