import React, { useState } from "react";
import './index.css'


const App = () => {

    let [Task, updateTask] = useState();
    let [newTask, updateNewTask] = useState();

    const InputEvent = (e) => {
        let Val = e.target.value;

    }
    
    const AddTask=(e)=>{
        e.preventDefault();
        let NewVal= updateNewTask(Task);
        console.log(NewVal);
    }
    

    return (
        <>
            <div className="main_div">
                <div className="center_div">
                    <br />
                    <h1>ToDo List</h1>
                    <br />
                    <input type="text" placeholder="Add Tasks" value={Task} onChange={InputEvent} />
                    <button onClick={AddTask}>+</button>
                    <ol>
                        <li>{newTask}</li>
                    </ol>
                </div>
            </div>
        </>
    )
}

export default App;
