import React, { useState } from "react";
import Note from "./NotesInput";
import SaveNote from "./ShowNote";

const App = () => {

    const [addItem, setAddItem] = useState()
    let AddNote = (note) => {
        setAddItem((preData) => {
            return [...preData, note]
        })
    }
    return (
        <>
            <Note passNote={AddNote} />
            {
                addItem.map((val, index) => {
                    return (
                        <SaveNote 
                            key={index}
                            id={index}
                            title={val.title}
                            content={val.content}

                        />
                    )
                })
            }
        </>
    )
}

export default App;
