import React, { useState } from "react";

const App = () => {

    let Col = "#e581e5";
    let Msg = "Click Me For Whises";
    let [bg, newBg] = useState(Col);
    let [currMsg, newMsg] = useState(Msg);

    const Change = () => {

        let Col = "#84ff84";
        let Msg = "LoL! Ye Kya Kr Diya ðŸ˜•";

        newBg(Col);
        newMsg(Msg);
    }

    const DblClick = () => {

        let Col = "#65f5ff";
        let Msg = "Bsdk! Mc ðŸ˜ ";

        newBg(Col);
        newMsg(Msg);
    }


    return (
        <>
            <div style={{ backgroundColor: bg }}>
                <button onDoubleClick={DblClick} onClick={Change}>{currMsg}</button>
            </div>
        </>
    )


}
export default App;
