import React, { useState } from "react";
import axios from 'axios';

const ComA = () => {
    const [num, setNum] = useState()

    getPokemon = async () => {

        const res = await axios.get(`https://pokeapi.co/api/v2/pokemon/${{num}}`);
        return res;
    }
    return (
        <>
            <h1>You Choose: {num}</h1>
            <h2>Pokeken :`${res.species.name}`</h2>
            <select value={num} onChange={(e) => {
                setNum(getPokemon())
            }}>
                <option>1</option>
                <option>2</option>
                <option>3</option>
                <option>4</option>
                <option>5</option>
            </select>
        </>
    )
}

export default ComA;
