import React, { useState } from "react";

const QA = ({ Q, A }) => {

    let [show, setShow] = useState(false);
    return (
        <>
            <div>
                <button onClick={() => setShow(true)}>{show ? '-' : '+'}</button>
                <h3>{Q}</h3>
            </div>
            {
                show && <p>{A}</p>
            }

        </>
    )
}
export default QA;
