import React from "react";
import ReactDOM from "react-dom";
// import Time from "./Time";


let time = new Date().getHours();
let Greet = " ";
let Css = " ";

function Time() {


    if (time >= 1 && time < 11) {
        Greet = "Good Morning";
        Css.color = "Orange";
    }
    else if (time > 12 && time < 19) {
        Greet = "Good Afternoon";
        Css.color = "red";
    }
    else {
        Greet = "Good Night";
        Css.color = "black";
    }

    return Greet;

}

export default Time;
