import React, { useState } from "react";

let d = new Date().toLocaleTimeString();
const App=()=>{

    let state = useState();
    let [Time , updatedTime] = useState(d);

    let CuurTime =()=>{
     updatedTime(new Date().toLocaleTimeString());
    //  console.log(`Time:${d} and ${new Date().toLocaleTimeString()} ` );
    }
     
    return(
        <>
            <h1>{Time}</h1>
            <button onClick={CuurTime}>Get Time</button>
        </>
    )



}

export default App;
