import React, { useState } from "react";
import ToDoLists from "./ToDo";
import './index.css'


const App = () => {

    let [inputList, setInputList] = useState();
    let [Items, setItems] = useState([]);

    const InputEvent = (e) => {
        setInputList(e.target.value)
    }

    const AddTask = (e) => {
        setItems((oldItems) => {
            return [...oldItems, inputList]
        });
        setInputList('')
    }

    let deleteItems=(id)=>{
        setItems((oldItems)=>{
            return oldItems.filter((e , index)=>{
                return index!== id;
            })
        })
            }

    return (
        <>
            <div className="main_div">
                <div className="center_div">
                    <br />
                    <h1>ToDo List</h1>
                    <br />
                    <input type="text" placeholder="Add Tasks" value={inputList} onChange={InputEvent} />
                    <button onClick={AddTask}>+</button>
                    <ol>
                        {Items.map((item,index) => {

                            return(
                            <ToDoLists
                                key={index}
                                id={index}
                                text={item}
                                onSelect={deleteItems}
                            />
                            )
                        })}
                    </ol>
                </div>
            </div>
        </>
    )
}

export default App;
