// Using Hooks Get Real Time On Click:-------


import React, { useState } from "react";

let d = new Date().toLocaleTimeString();
const App = () => {

    let state = useState();
    let [Time, updatedTime] = useState(d);
    
    setInterval(() => {
        let d = new Date().toLocaleTimeString();
        updatedTime(d.getSeconds)
    }, 1000);
    

    return (
        <>
            <h1>{Time}</h1>
        </>
    )



}

export default App;
