import React, { useState } from "react";

const Note = () => {

    const [Title, setTitle] = useState();
    const [MyNote, setMyNote] = useState();

    let AddNote=(e)=>{
        setTitle(e.target.value)
    }

    let AddNote2=(e)=>{
        setMyNote(e.target.value)
    }

    return (
        <>
            <form >
                <input type="text" placeholder="Enter Your Title" name="title" value={Title} onChange={AddNote} />
                <textarea placeholder="Write Your Note" rows="" column="" value={MyNote} onChange={AddNote2} />
                {/* <button onClick={AddNote}>+</button> */}
            </form>

            <div>
                <h3>Your Title:{Title}</h3>
                <p>Your Note:{MyNote}</p>
                <button>Del</button>
            </div>
        </>
    )
}

export default Note;
