import React, { useState } from "react";

const Note = () => {

    const [Title, setTitle] = useState();
    const [titleVal, setTitleVal] = useState([]);

    const [MyNote, setMyNote] = useState();
    const [newNote, serNewNote] = useState([]);

    let AddNote = (e) => {
        setTitleVal((oldVal) => {
            return [...oldVal, Title]
        })
        setTitleVal('')

    }


    return (
        <>
            <form >
                <input type="text" placeholder="Enter Your Title" name="title" value={Title} />
                <textarea placeholder="Write Your Note" rows="" column="" value={MyNote} />
                <button onClick={AddNote}>+</button>
            </form>

            <div>
                <h3>Your Title:{titleVal}</h3>
                <p>Your Note:{MyNote}</p>
                <button>Del</button>
            </div>
        </>
    )
}

export default Note;
