import React, { useState } from "react";

const App = () => {

    let [MyName, newName] = useState({
        fname: '',
        pass: '',
        email: '',
        num: '',
    });

    const InputEvent = (e) => {
        let Val = e.target.value;
        console.log(Val);

        const value = e.target.value;
        const Name = e.target.name;
        
        newName((preVal)=>{
            return{
                ...preVal,
                [Name]:value,
            }
        })


        
            
    
    }
    const ShowName = (e) => {
        e.preventDefault();
        alert('Submited')
    }

    return (
        <>
            <form onSubmit={ShowName}>
                <div>
                    <h1>Hello {MyName.fname} {MyName.pass}</h1>
                    <p>{MyName.email}</p>
                    <p>{MyName.num}</p>
                    <input placeholder="Enter Your Name" type="text" onChange={InputEvent} value={MyName.fName} name="fname" />
                    <input placeholder="Enter Your Password" type="text" onChange={InputEvent} value={MyName.Pass} name="pass" />
                    <input placeholder="Enter Your Email" type="email" onChange={InputEvent} value={MyName.Email} name="email" />
                    <input placeholder="Enter Your Phone" type="number" onChange={InputEvent} value={MyName.Num} name="num" />

                    <button type="submit">Submit</button>
                </div>
            </form>


        </>
    )
}

export default App;
