import React, { useState } from "react";

const Contact = () => {

  const [val, setVal] = useState({
    fullname: "",
    number: "",
    email: "",
    message: ""
  });

  const inputEvent = (e) => {

    setVal((preValue) => {
      return [
         val,...preValue
      ]

    })

  }


  return (
    <>
      <div className="my-3 ">
        <h2 className="text-center ">Contact Us</h2>
      </div>
      <div className="container mt-4">
        <div className="col-md-6 col-10 mx-auto ">
          <form>
            <div class="mb-3">
              <label for="exampleFormControlInput1" class="form-label">Full Name</label>
              <input type="text" class="form-control" id="exampleFormControlInput1" placeholder="Enter Your Name"
                name="fullname"
                value={val}
                onChange={inputEvent}
              />
            </div>
            <div class="mb-3">
              <label for="exampleFormControlInput1" class="form-label">Phone Number</label>
              <input type="number" class="form-control" id="exampleFormControlInput1" placeholder="Enter Your Phone Number"
                name="number"
                value={val}
                onChange={inputEvent}
              />
            </div>
            <div class="mb-3">
              <label for="exampleFormControlInput1" class="form-label">Email address</label>
              <input type="email" class="form-control" id="exampleFormControlInput1" placeholder="Enter Your Email"
                name="email"
                value={val}
                onChange={inputEvent}
              />
            </div>
            <div class="mb-3">
              <label for="exampleFormControlTextarea1" class="form-label">Example textarea</label>
              <textarea placeholder="Message" class="form-control" id="exampleFormControlTextarea1" rows="3"
                name="message"
                value={val}
                onChange={inputEvent}
              ></textarea>
            </div>
            <div class="col-auto">
              <button type="submit" class="btn btn-outline-primary  mb-3">Submit</button>
            </div>
          </form>
        </div>
      </div>
    </>
  )
}

export default Contact;
